// Create.jsx
import React, { useState } from 'react';
import './Create.css';
import API from '../../api/axios';
import { useNavigate } from 'react-router-dom';
const Create = ({ onClose, username }) => {
    let navigate=useNavigate();
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const [file, setFile] = useState(null);
    const [category, setCategory] = useState('');
    const [showCategoryOptions, setShowCategoryOptions] = useState(false);

    const gifUrl = 'data:image/jpeg;base64,';

    const categories = ['Poetry', 'Story', 'Drawing', 'Painting', 'Crafts','Dance','Music','Pottery'];


    const handleCategorySelect = (cat) => {
        setCategory(cat);
        setShowCategoryOptions(false);
    };

    const handleFileChange = (e) => {
        setFile(e.target.files[0]);
    };





    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!file) {
            alert('Please select an image to upload.');
            return;
        }

        if (!title) {
            alert('Please enter a title.');
            return;
        }

        const formData = new FormData();
        formData.append('image', file);
        formData.append('category', category);
        formData.append('title', title);
        formData.append('description', description);

        try {
            const token = localStorage.getItem('token');
            console.log(token);
            if (!token) {
                alert('You are not logged in.');
                return;
            }

            const response = await API.post('/posts', formData, {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            });

            alert('Post created successfully!');
            onClose();
        } catch (error) {
            console.error('Post creation error:', error);
            alert('Failed to create post.');
        }
    };


    return (
        <>
            <div className="modal-overlay" onClick={onClose}></div>
            <div className="create-container">
                {!category ? (
                    <>
                        <h2>Create a Post</h2>
                        <img src={gifUrl} alt="Welcome GIF style={{ width: '50%', height: 'auto', marginBottom: '20px',marginLeft:'100px' }" className="welcome-gif" />
                        <p>Welcome back. What do you want to post today?</p>

                        <button
                            onClick={() => setShowCategoryOptions(!showCategoryOptions)}
                            className="select-button"
                        >
                            Select Category
                        </button>
                        {showCategoryOptions && (
                            <div className="category-options">
                                {categories.map((cat) => (
                                    <div
                                        key={cat}
                                        className="category-option"
                                        onClick={() => handleCategorySelect(cat)}
                                    >
                                        {cat}
                                    </div>
                                ))}
                            </div>
                        )}
                    </>
                ) : (
                    <>
                        <h2>Selected: {category}</h2>

                        {/* File Upload Section */}
                        <div className="file-upload">
                            <label className="file-upload-button">
                                Choose Image
                                <input
                                    type="file"
                                    onChange={handleFileChange}
                                    accept="image/*"
                                />
                            </label>
                        </div>

                        {file && (
                            <>
                                <img
                                    src={URL.createObjectURL(file)}
                                    alt="Selected"
                                    className="selected-image"
                                />

                                {/* Title and Description appear only after file selection */}
                                <form onSubmit={handleSubmit} className="create-form">
                                    <input
                                        type="text"
                                        value={title}
                                        onChange={(e) => setTitle(e.target.value)}
                                        placeholder="Enter post title"
                                        required
                                    />
                                    <textarea
                                        value={description}
                                        onChange={(e) => setDescription(e.target.value)}
                                        placeholder="Write a description"
                                        rows="4"
                                        required
                                    ></textarea>
                                    <div className="button-group">
                                        <button type="submit" className="submit-button">
                                            Post
                                        </button>
                                        <button
                                            type="button"
                                            className="cancel-button"
                                            onClick={onClose}
                                        >
                                            Cancel
                                        </button>
                                    </div>
                                </form>
                            </>
                        )}
                    </>
                )}
            </div>
        </>
    );
};

export default Create;